package Java_Programs;

public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int x=4;
       int y=6;
       x=x+y;
       y= x-y;
       x= x-y;
       System.out.println("After swap");
       System.out.println("x=" +x);
       System.out.println("y=" +y);
	}

}
