package Java_Programs;

public class swap_temp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int x=4;
       int y=6;
       int temp;
       temp = x;
       x=y;
       y=temp;
       System.out.println("After swap");
       System.out.println("x=" +x);
       System.out.println("y=" +y);
	}

}